/* COMMON */
body {
    color : #333333;

    font-size: 16px;
    font-weight: 400;
    font-family: 'Nanum Gothic', sans-serif;

    line-height: 1.4;
}
img {
    display: block;
}
a {
    text-decoration: none;
}
.inner {
    width: 1100px;
    margin: 0 auto;
    position: relative;
}
.btn {
    width: 130px;
    padding: 10px;
    
    border: 2px solid #333333;
    border-radius: 4px;
    
    color: #333333;
    font-size: 16px;
    font-weight: 700;
    text-align: center;

    cursor: pointer;

    box-sizing: border-box;
    display: block;

    transition: 0.4s;

    &:hover {
        background-color: #333333;
        color: #ffffff;
    }

    &.btn--reverse {
        background-color: #333333;
        color: #ffffff;

        &:hover {
            background-color: transparent;
            color: #333333;
        }
    }

    &.btn--brown {
        color: #592B18;
        border-color: #592B18;
        
        &:hover {
            color: #ffffff;
            background-color: #592B18;
        }
    }

    &.btn--gold {
        color: #ffffff;
        border-color: #D9AABA;
        
        &:hover {
            color: #D9AABA;
            border-color: #ffffff;
        }
    }

    &.btn--white {
        color: #ffffff;
        border-color: #ffffff;

        &:hover {
            color: #ffffff;
            border-color: #333333;
        }
    }
}

.back-to-position {
    opacity: 0;
    transition: 1s;

    &.to-right {
        transform: translateX(-150px);
    }
    &.to-left {
        transform: translateX(150px);
    }
}

.show .back-to-position {
    opacity: 1;
    transform: translateX(0);

    @for $i from 0 through 3 {
        &.delay-#{ $i } {
            @if $i == 0 {
                transition-delay: 0s;
            }
            @else if $i == 1 {
                transition-delay: 0.3s;
            }
            @else if $i == 2 {
                transition-delay: 0.6s;
            }
            @else if $i == 3 {
                transition-delay: 0.9s;
            }
        }
    }
}


/* HEADER */
header {
    width: 100%;
    background-color: #f6f5f0;
    border-bottom: 1px solid #c8c8c8;

    position: fixed;
        top: 0;
    z-index: 10;
    
    > .inner {
        height: 120px;
    }

    .logo {
        height:  75px;
        position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
        margin: auto;
    }

    .sub-menu {
        display: flex;
        position: absolute;
            top: 10px;
            right: 0;

        ul.menu {
            display: flex;
            font-family: Arial, sans-serif;

            li {
                position: relative;

                &::before {
                    content: "";

                    width: 1px;
                    height: 12px;
                    background-color: #e5e5e5;

                    position: absolute;
                        top: 0;
                        bottom: 0;
                    margin: auto;
                }

                &:first-child::before {
                    display: none;
                }

                a {
                    font-size: 12px;
                    padding: 11px 16px;
                    color: #656565;
                    display: block;

                    &:hover {
                        color: #000000;
                    }
                }
            }
        }

        .search {
            height: 34px;
            position: relative;

            input {
                width: 36px;
                height: 34px;
                padding: 4px 10px;
            
                border: 1px solid #cccccc;
                box-sizing: border-box;
                border-radius: 5px;
            
                background-color: #ffffff;
                color: #777777;
                font-size: 12px;
            
                transition: width 0.4s;
                outline: none;

                &:focus {
                    width: 190px;
                    border-color: #669900;
                }
            }

            &.focused .material-icons {
                opacity: 0;
            }

            .material-icons {
                position: absolute;
                    top: 0;
                    bottom: 0;
                    right: 5px;
                margin: auto;
                
                height: 24px;
                transition: 0.8s;
            }
        }
    }

    .main-menu {
        display: flex;
        position: absolute;
            bottom: 0;
            right: 0;
        z-index: 1;

        .item .item__name {
            padding: 10px 20px 34px 20px;
            font-family: Arial, sans-serif;
            font-size: 13px;
        }

        .item:hover .item__name {
            background-color: #2c2a29;
            color: #669900;
        
            border-radius: 6px 6px 0 0;
        }

        .item .item__contents {
            width: 100%;
            position: fixed;
            left: 0;
            display: none;

            .contents__menu {
                background-color: #2c2a29;

                > ul {
                    display: flex;
                    padding: 20px 0;

                    > li{
                        width: 220px;

                        h4 {
                            padding: 3px 0 12px 0;
                            font-size: 14p;
                            color: #ffffff;
                        }

                        ul li {
                            padding: 5px 0;
                            font-size: 12px;
                            color : #999999;
                            cursor: pointer;

                            &:hover {
                                color: #669900;
                            }
                        }
                    }
                }
            }

            .contents__texture {
                background-image: url("../images/main_menu_pattern.jpg");
                padding: 26px 0;
                font-size: 12px;

                h4 {
                    color: #999999;
                    font-weight: 700;
                }

                p {
                    color: #669900;
                    margin: 4px 0 14px;
                }
            } 
        }

        .item:hover .item__contents {
            display: block;
        }

        
    }

    .badges {
        position: absolute;
            top: 132px;
            right: 12px;

            .badge {
                border-radius: 10px;
                overflow: hidden;
                margin-bottom: 12px;
                box-shadow: 4px 4px 10px rgba(0, 0, 0, 0.15);
                cursor: pointer;
            }
    }
}


/* FOOTER */
footer {
    background-color: #272727;
    border-top: 1px solid #333333;

    .inner {
        padding: 40px 0 60px 0;
    }

    .menu {
        display: flex;
        justify-content: center;

        li {
            position: relative;

            &::before {
                content: "";
                width: 3px;
                height: 3px;
                background-color: #555555;
                position: absolute;
                    top: 0;
                    bottom: 0;
