                display: none;
            }

            a {
                color: #cccccc;
                font-size: 12px;
                font-weight: 700;
                padding: 15px;
                display: block;

                &.green {
                    color: #669900;
                }
            }
        }
    }

    .btn-group {
        margin-top: 20px;
        display: flex;
        justify-content: center;

        .btn{
            font-size: 12px;
